	bytes32 public constant MINTER = keccak256("MINTER");
	bytes32 public constant CREATOR = keccak256("CREATOR");
}","keccak256":"0x0e0cff57250a7b36955e106678d96a52a5ff30f81daca4aec265e57b335087fc","license":"GPL-3.0"},"contracts/tokens/RAIR-721/AppStorage.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.25; 

library ERC721Storage {
    bytes32 internal constant STORAGE_SLOT =
        keccak256('rair.contracts.storage.RAIR721');

    struct product {
        uint startingToken;
        uint endingToken;
        uint mintableTokens;
        string name;
        uint[] rangeList;
    }

    struct range {
        uint rangeStart;
        uint rangeEnd;
        uint tokensAllowed;
        uint mintableTokens;
        uint lockedTokens;
        uint rangePrice;
        string rangeName;
    }

    struct Layout {
        // Base ERC721
        string _name;
        string _symbol;
        mapping(uint256 tokenId => address) _owners;
        mapping(address owner => uint256) _balances;
        mapping(uint256 tokenId => address) _tokenApprovals;
        mapping(address owner => mapping(address operator => bool)) _operatorApprovals;
        // ERC721 enumerable extension
        mapping(address owner => mapping(uint256 index => uint256)) _ownedTokens;
        mapping(uint256 tokenId => uint256) _ownedTokensIndex;
        uint256[] _allTokens;
        mapping(uint256 tokenId => uint256) _allTokensIndex;
        // RAIR exclusive
        string baseURI;
        address factoryAddress;
        uint16 royaltyFee;
        product[] products;
        range[] ranges;
        mapping(uint => uint) tokenToProduct;
        mapping(uint => uint) tokenToRange;
        mapping(uint => string) uniqueTokenURI;
        mapping(uint => string) productURI;
        mapping(uint => bool) appendTokenIndexToProductURI;
        bool appendTokenIndexToBaseURI;
        mapping(uint => uint[]) tokensByProduct;
        string contractMetadataURI;
        mapping(uint => uint) rangeToProduct;
        mapping(uint => bool) _minted;
        // August 2022 - Metadata File Extension Update
        mapping(uint => string) rangeURI;
        mapping(uint => bool) appendTokenIndexToRangeURI;
        string _metadataExtension;
        // March 2024 - Optional trader role
        bool requiresTrader;
    }

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}","keccak256":"0x5c1dacd45344afb85c863c85f27e20928c01951b96f3ac4096324b93183e0d31","license":"GPL-3.0"},"contracts/tokens/RAIR-721/Facets/ERC721EnumerableFacet.sol":{"content":"// SPDX-License-Identifier: MIT
